import React, { useState } from 'react';

function App() {
  const [activeNav, setActiveNav] = useState('Ïä§ÌÑ∞Îîî Î™©Î°ù');
  const [activeTab, setActiveTab] = useState('Upcoming');
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState(new Set([0, 2, 3])); // Ï≤´ Î≤àÏß∏, ÏÑ∏ Î≤àÏß∏, ÎÑ§ Î≤àÏß∏ Ïπ¥ÎìúÍ∞Ä Ï¢ãÏïÑÏöî ÏÉÅÌÉú
  const [currentPage, setCurrentPage] = useState('list');
  const [currentUser, setCurrentUser] = useState(null); // Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥
  const [joinedStudies, setJoinedStudies] = useState(new Set()); // ÏÇ¨Ïö©ÏûêÍ∞Ä Ï∞∏Ïó¨Ìïú Ïä§ÌÑ∞Îîî id Î™©Î°ù

  // Ï¥àÍ∏∞ Ïä§ÌÑ∞Îîî Îç∞Ïù¥ÌÑ∞
  const initialStudies = [
    {
      id: 0,
      title: 'Í∞ïÎÇ®Ïó≠ 9Î≤àÏ∂úÍµ¨ Ïä§ÌÉÄÎ≤ÖÏä§',
      time: '6Ïõî 5Ïùº 09:30',
      participantsCurrent: 3,
      participantsMax: 6,
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    },
    {
      id: 1,
      title: 'ÌôçÎåÄÏûÖÍµ¨Ïó≠ Ìï†Î¶¨Ïä§',
      time: '6Ïõî 5Ïùº 09:45',
      participantsCurrent: 8,
      participantsMax: 8,
      status: 'closed',
      statusText: 'ÎßàÍ∞ê',
      closed: true
    },
    {
      id: 2,
      title: 'Í±¥ÎåÄ ÎùºÏö¥ÏßÄÏª§ÌîºÏàç',
      time: '6Ïõî 9Ïùº 09:30',
      participantsCurrent: 3,
      participantsMax: 6,
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    },
    {
      id: 3,
      title: 'ÏòÅÎì±Ìè¨Ïó≠ Ìà¨Ïç∏ÌîåÎ†àÏù¥Ïä§',
      time: '6Ïõî 8Ïùº 09:30',
      participantsCurrent: 3,
      participantsMax: 6,
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    }
  ];

  // Ïä§ÌÑ∞Îîî Îç∞Ïù¥ÌÑ∞ state
  const [studies, setStudies] = useState(initialStudies);

  const handleNavClick = (navItem) => {
    setActiveNav(navItem);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleHeartClick = (studyId) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(studyId)) {
      newFavorites.delete(studyId);
    } else {
      newFavorites.add(studyId);
    }
    setFavorites(newFavorites);
  };

  const handleJoinClick = (studyId) => {
    if (!currentUser) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö©Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    const hasJoined = joinedStudies.has(studyId);

    setStudies(prevStudies => prevStudies.map(s => {
      if (s.id !== studyId) return s;

      // Ï∑®ÏÜå Î°úÏßÅ
      if (hasJoined) {
        const dec = Math.max(0, s.participantsCurrent - 1);
        const reopened = dec < s.participantsMax;
        return {
          ...s,
          participantsCurrent: dec,
          closed: reopened ? false : s.closed,
          status: reopened ? 'recruiting' : s.status,
          statusText: reopened ? 'Î™®ÏßëÏ§ë' : s.statusText
        };
      }

      // Ï∞∏Ïó¨ Î°úÏßÅ
      if (s.closed || s.participantsCurrent >= s.participantsMax) {
        alert('ÎßàÍ∞êÎêú Ïä§ÌÑ∞ÎîîÏûÖÎãàÎã§.');
        return s;
      }

      const inc = s.participantsCurrent + 1;
      const nowClosed = inc >= s.participantsMax;
      return {
        ...s,
        participantsCurrent: inc,
        closed: nowClosed,
        status: nowClosed ? 'closed' : s.status,
        statusText: nowClosed ? 'ÎßàÍ∞ê' : s.statusText
      };
    }));

    // joinedStudies ÏóÖÎç∞Ïù¥Ìä∏ Î∞è ÏïåÎ¶º
    setJoinedStudies(prev => {
      const newSet = new Set(prev);
      if (hasJoined) {
        newSet.delete(studyId);
        alert('Ï∞∏Ïó¨Í∞Ä Ï∑®ÏÜåÎêòÏóàÏäµÎãàÎã§.');
      } else {
        newSet.add(studyId);
        alert('Ïä§ÌÑ∞ÎîîÏóê Ï∞∏Ïó¨ Ïã†Ï≤≠ÎêòÏóàÏäµÎãàÎã§!');
      }
      return newSet;
    });
  };

  const handleCreateStudyClick = () => {
    setCurrentPage('create');
  };

  const handleLoginClick = () => {
    setCurrentPage('login');
  };

  const handleSignUpClick = () => {
    setCurrentPage('signup');
  };

  const handleBackClick = () => {
    setCurrentPage('list');
  };

  const filteredStudies = studies.filter(study =>
    study.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (currentPage === 'create') {
    return (
      <div className="create-page">
        <header className="create-header">
          <button className="back-btn" onClick={handleBackClick}>‚Üê Back</button>
          <h1 className="create-title">Ïä§ÌÑ∞Îîî Í∞úÏÑ§</h1>
        </header>
        <form
          className="create-form"
          onSubmit={e => {
            e.preventDefault();
            const data = new FormData(e.target);
            const title = data.get('title');
            const place = data.get('place');
            const date = data.get('date');
            const time = data.get('time');
            const maxPeople = data.get('max');

            // Í∞ÑÎã®Ìïú ÎÇ†Ïßú¬∑ÏãúÍ∞Ñ Î¨∏ÏûêÏó¥ Ï°∞Ìï© (YYYY-MM-DD HH:MM)
            const timeText = `${date} ${time}`;

            const newId = studies.length ? Math.max(...studies.map(s => s.id)) + 1 : 0;
            const newStudy = {
              id: newId,
              title: place,
              time: timeText,
              participantsCurrent: 0,
              participantsMax: Number(maxPeople),
              status: 'recruiting',
              statusText: 'Î™®ÏßëÏ§ë',
              closed: false
            };

            setStudies([...studies, newStudy]);

            alert('Ïä§ÌÑ∞ÎîîÍ∞Ä Í∞úÏÑ§ÎêòÏóàÏäµÎãàÎã§!');
            setCurrentPage('list');
          }}
        >
          <label>
            Ïä§ÌÑ∞Îîî Ï†úÎ™©
            <input type="text" name="title" placeholder="Ïòà) ÏïåÍ≥†Î¶¨Ï¶ò Ïä§ÌÑ∞Îîî" required />
          </label>
          <label>
            Ïû•ÏÜå
            <input type="text" name="place" placeholder="Ïòà) Í∞ïÎÇ®Ïó≠ 9Î≤à Ï∂úÍµ¨ Ïä§ÌÉÄÎ≤ÖÏä§" required />
          </label>
          <label>
            ÎÇ†Ïßú
            <input type="date" name="date" required />
          </label>
          <label>
            ÏãúÍ∞Ñ
            <input type="time" name="time" required />
          </label>
          <label>
            ÏµúÎåÄ Ïù∏Ïõê
            <input type="number" name="max" min="2" max="50" defaultValue={6} required />
          </label>
          <label>
            ÏÑ§Î™Ö
            <textarea name="desc" rows="5" placeholder="Ïä§ÌÑ∞ÎîîÏóê ÎåÄÌïú ÏÑ§Î™ÖÏùÑ ÏûëÏÑ±ÌïòÏÑ∏Ïöî" />
          </label>
          <button className="submit-btn" type="submit">Í∞úÏÑ§ÌïòÍ∏∞</button>
        </form>
      </div>
    );
  }

  if (currentPage === 'signup') {
    return (
      <div className="auth-page">
        <header className="auth-header">
          <button className="back-btn" onClick={handleBackClick}>‚Üê Back</button>
          <h1 className="auth-title">Sign Up</h1>
        </header>
        <form
          className="auth-form"
          onSubmit={e => {
            e.preventDefault();
            alert('ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
            setCurrentPage('list');
          }}
        >
          <label>
            Ïù¥Î¶Ñ
            <input type="text" name="name" placeholder="ÌôçÍ∏∏Îèô" required />
          </label>
          <label>
            Ïù¥Î©îÏùº
            <input type="email" name="email" placeholder="example@example.com" required />
          </label>
          <label>
            ÎπÑÎ∞ÄÎ≤àÌò∏
            <input type="password" name="password" required />
          </label>
          <label>
            ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏
            <input type="password" name="passwordConfirm" required />
          </label>
          <button className="submit-btn" type="submit">ÌöåÏõêÍ∞ÄÏûÖ</button>
        </form>
      </div>
    );
  }

  if (currentPage === 'login') {
    return (
      <div className="auth-page">
        <header className="auth-header">
          <button className="back-btn" onClick={handleBackClick}>‚Üê Back</button>
          <h1 className="auth-title">Login</h1>
        </header>
        <form
          className="auth-form"
          onSubmit={e => {
            e.preventDefault();
            alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
            setCurrentPage('list');
          }}
        >
          <label>
            Ïù¥Î©îÏùº
            <input type="email" name="email" placeholder="example@example.com" required />
          </label>
          <label>
            ÎπÑÎ∞ÄÎ≤àÌò∏
            <input type="password" name="password" required />
          </label>
          <button className="submit-btn" type="submit">Î°úÍ∑∏Ïù∏</button>
        </form>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <aside className="sidebar">
        <div className="logo" onClick={() => window.location.reload()}>
          <div className="logo-icon"></div>
          <span className="logo-text">Study</span>
        </div>

        <nav className="nav-menu">
          {['Ïä§ÌÑ∞Îîî Î™©Î°ù', 'Ïä§ÌÑ∞Îîî ÌõÑÍ∏∞', 'ÎßàÏù¥ÌéòÏù¥ÏßÄ'].map((item, index) => (
            <div 
              key={item}
              className={`nav-item ${activeNav === item ? 'active' : ''}`}
              onClick={() => handleNavClick(item)}
            >
              <div className={`nav-icon ${
                index === 0 ? 'overview-icon' :
                index === 1 ? 'insights-icon' :
                index === 2 ? 'mypage-icon' : ''
              }`}></div>
              <span>{item}</span>
            </div>
          ))}
        </nav>

        <button className="create-study-btn" onClick={handleCreateStudyClick}>
          <span>Ïä§ÌÑ∞Îîî Í∞úÏÑ§ÌïòÍ∏∞</span>
        </button>

        <div className="dashboard-image"></div>
      </aside>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <main className="main-content">
        {/* ÏÉÅÎã® Ìó§Îçî */}
        <header className="header">
          <div className="search-bar">
            <div className="search-icon"></div>
            <input 
              type="text" 
              placeholder="Search"
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>
          <div className="auth-buttons">
            <button className="login-btn" onClick={handleLoginClick}>Login</button>
            <button className="signup-btn" onClick={handleSignUpClick}>Sign Up</button>
          </div>
        </header>

        {/* ÏΩòÌÖêÏ∏† ÏòÅÏó≠ */}
        <div className="content">
          <h1 className="page-title">Ïä§ÌÑ∞Îîî Î™©Î°ù</h1>

          <nav className="tab-nav">
            {['Upcoming', 'Comment'].map(tab => (
              <div 
                key={tab}
                className={`tab ${activeTab === tab ? 'active' : ''}`}
                onClick={() => handleTabClick(tab)}
              >
                {tab}
              </div>
            ))}
          </nav>

          {/* Ïä§ÌÑ∞Îîî Ïπ¥Îìú Î™©Î°ù */}
          <div className="study-list">
            {filteredStudies.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '40px', color: '#AAB2C8' }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>üîç</div>
                <div style={{ fontSize: '18px' }}>Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.</div>
              </div>
            ) : (
              filteredStudies.map((study) => (
                <div key={study.id} className={`study-card ${study.closed ? 'closed' : ''}`}>
                  <div className="card-content">
                    <div className="study-info">
                      <h3>{study.title}</h3>
                      <p className="study-time">{study.time}</p>
                    </div>
                    <div className="study-meta">
                      <div className="favorites" onClick={() => handleHeartClick(study.id)}>
                        <div className={`heart-icon ${favorites.has(study.id) ? 'filled' : ''}`}></div>
                      </div>
                      <div className="participants">
                        <div className="user-icon"></div>
                        <span>{`${study.participantsCurrent}/${study.participantsMax}`}</span>
                        <span className="separator">-</span>
                        <div className="location-icon"></div>
                      </div>
                      <div className="rating">
                        <div className="star-icon"></div>
                        <span>Average rating</span>
                      </div>
                      <div className={`status ${study.status}`}>
                        <div className="status-icon"></div>
                        <span>{study.statusText}</span>
                      </div>
                      <button
                        className="join-btn"
                        onClick={() => handleJoinClick(study.id)}
                      >
                        {joinedStudies.has(study.id) ? 'Ï∞∏Ïó¨ Ï∑®ÏÜå' : 'Ï∞∏Ïó¨ÌïòÍ∏∞'}
                      </button>
                    </div>
                  </div>
                  <div className="card-menu" onClick={() => alert('Î©îÎâ¥ ÏòµÏÖòÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.')}>
                    <div className="menu-icon"></div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
