import React, { useState } from 'react';

function App() {
  const [activeNav, setActiveNav] = useState('Ïä§ÌÑ∞Îîî Î™©Î°ù');
  const [activeTab, setActiveTab] = useState('Upcoming');
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState(new Set([0, 2, 3])); // Ï≤´ Î≤àÏß∏, ÏÑ∏ Î≤àÏß∏, ÎÑ§ Î≤àÏß∏ Ïπ¥ÎìúÍ∞Ä Ï¢ãÏïÑÏöî ÏÉÅÌÉú

  const studyData = [
    {
      id: 0,
      title: 'Í∞ïÎÇ®Ïó≠ 9Î≤àÏ∂úÍµ¨ Ïä§ÌÉÄÎ≤ÖÏä§',
      time: '6Ïõî 5Ïùº 09:30',
      participants: '3/6',
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    },
    {
      id: 1,
      title: 'ÌôçÎåÄÏûÖÍµ¨Ïó≠ Ìï†Î¶¨Ïä§',
      time: '6Ïõî 5Ïùº 09:45',
      participants: '8/8',
      status: 'closed',
      statusText: 'ÎßàÍ∞ê',
      closed: true
    },
    {
      id: 2,
      title: 'Í±¥ÎåÄ ÎùºÏö¥ÏßÄÏª§ÌîºÏàç',
      time: '6Ïõî 9Ïùº 09:30',
      participants: '3/6',
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    },
    {
      id: 3,
      title: 'ÏòÅÎì±Ìè¨Ïó≠ Ìà¨Ïç∏ÌîåÎ†àÏù¥Ïä§',
      time: '6Ïõî 8Ïùº 09:30',
      participants: '3/6',
      status: 'recruiting',
      statusText: 'Î™®ÏßëÏ§ë',
      closed: false
    }
  ];

  const handleNavClick = (navItem) => {
    setActiveNav(navItem);
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleHeartClick = (studyId) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(studyId)) {
      newFavorites.delete(studyId);
    } else {
      newFavorites.add(studyId);
    }
    setFavorites(newFavorites);
  };

  const handleJoinClick = (study) => {
    if (study.closed) {
      alert('ÎßàÍ∞êÎêú Ïä§ÌÑ∞ÎîîÏûÖÎãàÎã§.');
      return;
    }
    alert(`"${study.title}" Ïä§ÌÑ∞ÎîîÏóê Ï∞∏Ïó¨ Ïã†Ï≤≠ÎêòÏóàÏäµÎãàÎã§!`);
  };

  const handleCreateStudyClick = () => {
    alert('Ïä§ÌÑ∞Îîî Í∞úÏÑ§ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.');
  };

  const handleLoginClick = () => {
    alert('Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.');
  };

  const handleSignUpClick = () => {
    alert('ÌöåÏõêÍ∞ÄÏûÖ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.');
  };

  const filteredStudies = studyData.filter(study =>
    study.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="dashboard">
      {/* ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <aside className="sidebar">
        <div className="logo" onClick={() => window.location.reload()}>
          <div className="logo-icon"></div>
          <span className="logo-text">Study</span>
        </div>

        <nav className="nav-menu">
          {['Ïä§ÌÑ∞Îîî Î™©Î°ù', 'Ïä§ÌÑ∞Îîî ÌõÑÍ∏∞', 'ÎßàÏù¥ÌéòÏù¥ÏßÄ'].map((item, index) => (
            <div 
              key={item}
              className={`nav-item ${activeNav === item ? 'active' : ''}`}
              onClick={() => handleNavClick(item)}
            >
              <div className={`nav-icon ${
                index === 0 ? 'overview-icon' :
                index === 1 ? 'insights-icon' :
                index === 2 ? 'mypage-icon' : ''
              }`}></div>
              <span>{item}</span>
            </div>
          ))}
        </nav>

        <button className="create-study-btn" onClick={handleCreateStudyClick}>
          <span>Ïä§ÌÑ∞Îîî Í∞úÏÑ§ÌïòÍ∏∞</span>
        </button>

        <div className="dashboard-image"></div>
      </aside>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <main className="main-content">
        {/* ÏÉÅÎã® Ìó§Îçî */}
        <header className="header">
          <div className="search-bar">
            <div className="search-icon"></div>
            <input 
              type="text" 
              placeholder="Search"
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>
          <div className="auth-buttons">
            <button className="login-btn" onClick={handleLoginClick}>Login</button>
            <button className="signup-btn" onClick={handleSignUpClick}>Sign Up</button>
          </div>
        </header>

        {/* ÏΩòÌÖêÏ∏† ÏòÅÏó≠ */}
        <div className="content">
          <h1 className="page-title">Ïä§ÌÑ∞Îîî Î™©Î°ù</h1>

          <nav className="tab-nav">
            {['Upcoming', 'Comment'].map(tab => (
              <div 
                key={tab}
                className={`tab ${activeTab === tab ? 'active' : ''}`}
                onClick={() => handleTabClick(tab)}
              >
                {tab}
              </div>
            ))}
          </nav>

          {/* Ïä§ÌÑ∞Îîî Ïπ¥Îìú Î™©Î°ù */}
          <div className="study-list">
            {filteredStudies.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '40px', color: '#AAB2C8' }}>
                <div style={{ fontSize: '48px', marginBottom: '16px' }}>üîç</div>
                <div style={{ fontSize: '18px' }}>Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.</div>
              </div>
            ) : (
              filteredStudies.map((study) => (
                <div key={study.id} className={`study-card ${study.closed ? 'closed' : ''}`}>
                  <div className="card-content">
                    <div className="study-info">
                      <h3>{study.title}</h3>
                      <p className="study-time">{study.time}</p>
                    </div>
                    <div className="study-meta">
                      <div className="favorites" onClick={() => handleHeartClick(study.id)}>
                        <div className={`heart-icon ${favorites.has(study.id) ? 'filled' : ''}`}></div>
                      </div>
                      <div className="participants">
                        <div className="user-icon"></div>
                        <span>{study.participants}</span>
                        <span className="separator">-</span>
                        <div className="location-icon"></div>
                      </div>
                      <div className="rating">
                        <div className="star-icon"></div>
                        <span>Average rating</span>
                      </div>
                      <div className={`status ${study.status}`}>
                        <div className="status-icon"></div>
                        <span>{study.statusText}</span>
                      </div>
                      <button className="join-btn" onClick={() => handleJoinClick(study)}>
                        Ï∞∏Ïó¨ÌïòÍ∏∞
                      </button>
                    </div>
                  </div>
                  <div className="card-menu" onClick={() => alert('Î©îÎâ¥ ÏòµÏÖòÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.')}>
                    <div className="menu-icon"></div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
